			glColor3f(0, 1, 0);
			
			glTexCoord2f(0.34f, 0.0f);
			glVertex3f(0, 0, 1);
			glTexCoord2f(0.66f, 0.0f);
			glVertex3f(0, 1, 1);
			glTexCoord2f(0.66f, 0.99f);
